import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import Icon from "@material-ui/core/Icon";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import "../CSS/Week.css";

class Week extends Component {
  constructor(props) {
    super(props);
    var date = new Date();
    this.state = {
      date: date,
      day: date.getDate(),
      month: date.getMonth() + 1,
      week_change: 0
    };
  }

  WeekBack = () => {
    var cueent_date = this.state.date;
    console.log(cueent_date.toUTCString());

    this.setState({
      date: cueent_date.setDate(this.state.day - 7 + (7 - (this.state.day % 7)))
    });

    console.log("hey" + this.state.date);
  };

  render() {
    return (
      <div>
        <div>{this.WeekDates}</div>
        <div>
          <Button
            className="lefty"
            variant="contained"
            color="primary"
            startIcon={<ArrowBackIosIcon />}
            onClick={this.WeekBack}
          >
            Back
          </Button>
        </div>
        <div>
          <Button
            variant="contained"
            color="primary"
            className="righty"
            endIcon={<ArrowForwardIosIcon />}
            onClick={this.WeekBack}
          >
            s
          </Button>
        </div>
      </div>
    );
  }
}

export default Week;
