import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import Icon from "@material-ui/core/Icon";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import "../CSS/Week.css";

class Week extends Component {
  constructor(props) {
    super(props);
    this.state = {
      date: new Date(),
      week_change: 0
    };
  }
  WeekDates = () => {
    console.log("pass");
    return "ss";
  };
  WeekBack = () => {
    console.log(this.state.date);
    this.setState({
      date: this.state.date.setDate(
        this.state.date.getDate() - 7 + ((7 - this.state.date.getDay()) % 7)
      )
    });
    console.log(this.state.date.getUTCDate());
  };

  render() {
    return (
      <div>
        <h1>{this.WeekDates}</h1>
        <div>
          <Button
            className="lefty"
            variant="contained"
            color="primary"
            startIcon={<ArrowBackIosIcon />}
            onClick={this.WeekBack}
          >
            Back
          </Button>
        </div>
        <div>
          <Button
            variant="contained"
            color="primary"
            className="righty"
            endIcon={<ArrowForwardIosIcon />}
            onClick={this.WeekBack}
          >
            s
          </Button>
        </div>
      </div>
    );
  }
}

export default Week;
