import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import Icon from "@material-ui/core/Icon";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import "../CSS/Week.css";

class Week extends Component {
  constructor(props) {
    super(props);
    var date = new Date();
    this.state = {
      date: date,
      day: date.getDate(),
      month: date.getMonth() + 1
    };
  }
  showdate() {
    return <h1>{this.state.date}</h1>;
  }
  WeekBack = () => {
    var cueent_date = this.state.date;
    console.log("the day is " + this.state.day);
    cueent_date.setDate(this.state.day - 7 + (7 - (this.state.day % 7)));
    this.setState({
      date: cueent_date.toUTCString
    });
    this.setState({ day: this.state.date.getDate() });
    console.log("hey" + this.state.date);
  };
  nextSunday = () => {
    let sunday_date = new Date();
    sunday_date.setDate(
      this.state.date.getDate() + (7 + (this.state.date.getDay() % 7))
    );
    let day = sunday_date.getDate();
    let month = sunday_date.getMonth() + 1;
    return day + "/" + month;
  };

  getThisSunday = () => {
    let sunday_date = new Date();
    sunday_date.setDate(
      this.state.date.getDate() - (this.state.date.getDay() % 7)
    );
    let day = sunday_date.getDate();
    let month = sunday_date.getMonth() + 1;
    return day + "/" + month;
  };
  render() {
    return (
      <div>
        <h1>
          {this.getThisSunday()}
          {"-"} {this.nextSunday()}
        </h1>
        <div>
          <Button
            className="lefty"
            variant="contained"
            color="primary"
            startIcon={<ArrowBackIosIcon />}
            onClick={this.WeekBack}
          >
            Back
          </Button>
        </div>
        <div>
          <Button
            variant="contained"
            color="primary"
            className="righty"
            endIcon={<ArrowForwardIosIcon />}
            onClick={this.WeekBack}
          >
            s
          </Button>
        </div>
      </div>
    );
  }
}

export default Week;
