import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import Icon from "@material-ui/core/Icon";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import "../CSS/Week.css";
import Toranim from "./Toranim";
import moment from "moment";

class Week extends Component {
  constructor(props) {
    super(props);
    var date = new Date();
    this.state = {
      date: date,
      start_date: null,
      start_date_str: null,
      end_date: null,
      end_date_str: null
    };
  }
  componentWillMount() {
    this.getCurrentWeek();
  }
  componentDidMount() {
    console.log("www");
  }
  componentDidUpdate() {
    console.log("hhh");
  }

  getCurrentWeek = () => {
    var currentDate = moment();

    var weekStart = currentDate.clone().startOf("week");

    var days = [];

    for (var i = 0; i <= 6; i++) {
      days.push(
        moment(weekStart)
          .add(i, "days")
          .format("D.MM")
      );
    }
    console.log(days);

    this.setState({
      date: days
    });
    console.log("ttttt");
    console.log(this.state.date);
  };

  setWeekBack = () => {
    let week_ago_momnet = moment()
      .subtract(1, "weeks")
      .startOf("week");
    var days = [];

    for (var i = 0; i <= 6; i++) {
      days.push(
        moment(week_ago_momnet)
          .add(i, "days")
          .format("D.MM")
      );
    }
    // console.log(days);
    this.setState({
      date: days
    });
  };

  render() {
    return (
      <div>
        <h1 className="headerDate">
          {this.state.start_date_str}
          {"-"} {this.state.end_date_str}
        </h1>
        <div>
          <Button
            className="lefty"
            variant="contained"
            color="primary"
            startIcon={<ArrowBackIosIcon />}
            onClick={this.WeekBack}
          >
            Back
          </Button>
        </div>
        <div>
          <Button
            variant="contained"
            color="primary"
            className="righty"
            endIcon={<ArrowForwardIosIcon />}
            onClick={this.WeekBack}
          >
            s
          </Button>
        </div>
        <div>
          <Toranim date={this.state.date} />
        </div>
      </div>
    );
  }
}

export default Week;
