  };

  setDates = () => {
    console.log("passed");
    console.log(this.props.date);
    this.setState({
      dates: this.props.date
    });
  };
  generateTable = () => {
    let res = [];

    res.push(<tr key="date_header">{this.setForEachDay()}</tr>);

    return res;
  };

  getToranimCurrentWeek(dates) {
