  }
  setForEachDay = () => {
    let dates = this.props.date;
    let week_items = [];

    var toranim_cur_week = this.getToranimCurrentWeek(dates);
    week_items.push(<th key="empty">Toran</th>);
    for (var i = 0; i < dates.length; i++) {
      week_items.push(
        <td key={dates[i]}>
          <Select
            id={dates[i]}
            value={{ value: true, label: "g" }}
            onChange={this.handleChange}
            options={this.addOptions(dates[i])}
            components={animatedComponents}
          ></Select>
        </td>
      );
    }
    week_items.push(
      <td key="Yashvatz">
        <Select
          id="Yashvatz"
          defaultValue="-1"
          onChange={this.handleChange}
          options={this.addOptions(
            "yashvatz-" + dates[0] + "-" + dates[dates.length - 1]
          )}
          components={animatedComponents}
        ></Select>
      </td>
    );

    return week_items;
  };

  addOptions = date => {
    let items = [];

    for (var i = 0; i < toranim.length; i++) {
      items.push({
        label: toranim[i],
        value: date,
        key: i
      });
    }
    return items;
  };

  generateHeader = cur_date => {
    let header_days = [];
    header_days.push(<th key="empty"></th>);
    for (var i = 0; i < cur_date.length + 1; i++) {
      if (i != cur_date.length) {
        header_days.push(
          <th className="toran_days" key={cur_date[i]}>
            {week_days[i] + "\n" + cur_date[i]}
          </th>
        );
      } else {
        header_days.push(
          <th className="toran_days" key="Yashvtaz">
            Weekly Yachvatz
          </th>
        );
      }
    }
    return header_days;
  };

