import React, { Component } from "react";
import Select, { components } from "react-select";
import "bootstrap/dist/css/bootstrap.css";
import Table from "react-bootstrap/Button";
import "../CSS/Toranim.css";
import DataFetch from "./DataFetch";
import { MDBTable, MDBTableBody, MDBTableHead } from "mdbreact";

const toranim = ["A", "B", "C", "D", "E"];
// const animatedComponents = makeAnimated();
const week_days = [
  "Sunday",
  "Monday",
  "Tuesday",
  "Wendsday",
  "Thursday",
  "Friday",
  "Saturday"
];
const { Option } = Select;

const addOptions = (date, day) => {
  let items = [];

  for (var i = 0; i < toranim.length; i++) {
    // items.push({ label: toranim[i], value: date, key: i, day: day });
    items.push(
      <option label={toranim[i]} value={date} key={i} day={day}>
        {toranim[i]}
      </option>
    );
  }

  return items;
};

class Toranim extends Component {
  constructor(props) {
    super(props);
    var date = new Date();
    this.state = {
      dates: props.date,
      toran_days: null,
      is_fetching: true
    };
    console.log("ooooooooooooo");
  }

  handleChange = target_toran => {
    console.log("hettt");
    let index = target_toran.nativeEvent.target.selectedIndex;
    let label = target_toran.nativeEvent.target[index].text;
    let value = target_toran.target.value;
    console.log(label, value);
    var url = "http://localhost:5000/set_toran";
    let toran_json_data = { label: label, value: value };
    fetch(url, {
      method: "post",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },

      body: JSON.stringify(toran_json_data)
    }).then(res => {
      console.log(res);
    });
  };
  // static getDerivedStateFromProps(nextProps, prevState) {
  //   console.log("nopeeee");
  //   console.log(prevState.dates, this.props.dates);
  //   // if (nextProps.someValue !== prevState.someValue) {
  //   //   return { someState: nextProps.someValue };
  //   // } else return null;
  // }
  generateTable() {
    let res = [];
    let dates = this.props.date;
    let result_toranim_cur_week;
    // result_toranim_cur_week = await table_data_toranim(dates);
    console.log("hhhhhhhhhhhhhhhhhhhh");
    console.log(result_toranim_cur_week);
    res.push(
      <tr key="date_header">{this.setForEachDay(result_toranim_cur_week)}</tr>
    );
    console.log(res);
    return res;
  }

  getVlaueFromKey = (object, value_by_key) => {
    console.log("passed here");
    var a = JSON.parse(JSON.stringify(object));
    console.log(a.value_by_key);
    return object.value_by_key;
  };

  setForEachDay = toranim_this_week => {
    let week_items = [];
    let dates = this.props.date;
    console.log("rendered again");
    week_items.push(<th key="empty">Toran</th>);
    for (var i = 0; i < dates.length; i++) {
      week_items.push(
        <td key={dates[i]}>
          <select
            value={
              // Object.keys(toranim_this_week).includes(dates[i])
              //   ? toranim_this_week[dates[i]]
              //   : "DEFAULT"
              "ffff"
            }
            onChange={this.handleChange}
            className="form-control"
          >
            <option value="DEFAULT" disabled>
              Select Toran...{" "}
            </option>
            {addOptions(dates[i], i)}
          </select>
        </td>
      );
    }
    week_items.push(
      <td key="Yashvatz">
        <select onChange={this.handleChange} className="form-control">
          <option selected disabled>
            Select Toran...{" "}
          </option>
          {addOptions(
            "yashvatz-" + dates[0] + "-" + dates[dates.length - 1],
            7
          )}{" "}
        </select>
      </td>
    );
    console.log(week_items[0]);
    return week_items;
  };

  generateHeader = cur_date => {
    let header_days = [];
    header_days.push(<th key="empty"></th>);
    for (var i = 0; i < cur_date.length + 1; i++) {
      if (i != cur_date.length) {
        header_days.push(
          <th align="center" className="toran_days" key={cur_date[i]}>
            {week_days[i] + "\n" + cur_date[i]}
          </th>
        );
      } else {
        header_days.push(
          <th align="center" className="toran_days" key="Yashvtaz">
            Weekly Yachvatz
          </th>
        );
      }
    }
    return header_days;
  };
  componentDidUpdate(previousProps, previousState) {
    console.log(previousProps.date, previousState.dates, this.props.dates);
    if (previousProps.data !== this.props.data) {
      console.log("semec ars");
    }
  }

  componentDidMount() {
    let dates = this.state.dates;
    var url = new URL("http://localhost:5000/get_toranim");
    var params = { dates: dates };

    url.search = new URLSearchParams(params).toString();
    console.log(url);
    fetch(url)
      .then(res => res.json())
      .then(toran_days => this.setState({ toran_days }));
    // this.setState({ toranim_schedule }));
    console.log("rrrrrrr");
  }

  render() {
    var cur_date = this.props.date;
    console.log("lol");

    return (
      <div>
        {console.log(cur_date)}
        <MDBTable striped bordered>
          <MDBTableHead color="primary-color">
            <tr>{this.generateHeader(this.props.date)}</tr>
          </MDBTableHead>
          <MDBTableBody>{this.generateTable()}</MDBTableBody>
        </MDBTable>
      </div>
    );
  }
}

export default Toranim;
