import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import Icon from "@material-ui/core/Icon";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import "../CSS/Week.css";

class Week extends Component {
  constructor(props) {
    super(props);
    var date = new Date();
    this.state = {
      date: date,
      this_sunday_date: date.getDate() - (date.getDay() % 7),
      start_date: null,
      start_date_str: null,
      end_date: null,
      end_date_str: null
    };
  }

  WeekBack = () => {
    console.log("gg");
  };

  componentDidMount() {
    console.log("www");
    this.getThisSunday();
    this.nextSunday();
    console.log(this.state.end_date + "-k-" + this.state.start_date);
  }
  componentDidUpdate() {
    console.log("hhh");
  }
  nextSunday = () => {
    let next_sunday_date = this.state.date;
    next_sunday_date.setDate(
      this.state.date.getDate() + (7 - (this.state.date.getDay() % 7))
    );
    let day = next_sunday_date.getDate();
    let month = next_sunday_date.getMonth() + 1;
    this.setState({
      end_date_str: day + "/" + month,
      end_date: next_sunday_date
    });
    return day + "/" + month;
  };

  getThisSunday = () => {
    let sunday_date = this.state.date;
    sunday_date.setDate(
      this.state.date.getDate() - (this.state.date.getDay() % 7)
    );

    console.log("this is sunday " + sunday_date);
    let day = sunday_date.getDate();
    let month = sunday_date.getMonth() + 1;

    this.setState({
      start_date_str: day + "/" + month,
      start_date: sunday_date
    });
  };
  render() {
    return (
      <div>
        <h1>
          {this.state.start_date_str}
          {"-"} {this.state.end_date_str}
        </h1>
        <div>
          <Button
            className="lefty"
            variant="contained"
            color="primary"
            startIcon={<ArrowBackIosIcon />}
            onClick={this.WeekBack}
          >
            Back
          </Button>
        </div>
        <div>
          <Button
            variant="contained"
            color="primary"
            className="righty"
            endIcon={<ArrowForwardIosIcon />}
            onClick={this.WeekBack}
          >
            s
          </Button>
        </div>
      </div>
    );
  }
}

export default Week;
