import React, { Component } from "react";
import Select, { components } from "react-select";
import makeAnimated from "react-select/animated";
import moment from "moment";
import Axios from "axios";

const toranim = ["A", "B", "C", "D", "E"];
const animatedComponents = makeAnimated();
const week_days = [
  "Sunday",
  "Monday",
  "Tuesday",
  "Wendsday",
  "Thursday",
  "Friday",
  "Saturday"
];

const getParams = obj => {
  const params = new URLSearchParams();
  const keys = Object.keys(obj);
  for (let k of keys) {
    params.append(k, obj[k]);
  }
  return params;
};

class Toranim extends Component {
  constructor(props) {
    super(props);
    var date = new Date();
    this.state = { dates: props.date, toran_daily: "" };
  }
  componentWillMount() {
    console.log("ytred");
  }
  handleChange = target_toran => {
    console.log(target_toran);
    var url = "http://localhost:5000/set_toran";
    // var params = { toran_date: target_toran };
    // url.search = new URLSearchParams(target_toran).toString();

    var options = {
      method: "POST",
      body: JSON.stringify(target_toran),
      headers: {
        "Content-Type": "application/json"
      }
    };
    // this.setState({ toran_daily: target_toran["label"] });
    fetch(url, {
      method: "post",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json"
      },

      //make sure to serialize your JSON body
      body: JSON.stringify(target_toran)
    }).then(res => {
      console.log(res.data);
    });
  };

  setDates = () => {
    console.log("passed");
    console.log(this.props.date);
    this.setState({
      dates: this.props.date
    });
  };
  generateTable = () => {
    let res = [];

    res.push(<tr key="date_header">{this.setForEachDay()}</tr>);

    return res;
  };

  getToranimCurrentWeek(dates) {
    let dates_list = [];
    console.log(dates);

    dates.map(x => dates_list.push(x));
    // for (var key in dates) {
    //   dates_list.push(dates[key]);
    // }
    let payload = {
      dates: dates_list
    };
    console.log(dates_list);

    var url = new URL("http://localhost:5000/get_toranim");

    var params = { dates: dates };
    url.search = new URLSearchParams(params).toString();

    // Axios.get("http://localhost:5000/get_toranim", {
    // data: payload
    fetch(url).then(res => {
      console.log(res.data);
    });
  }
  setForEachDay = () => {
    let dates = this.props.date;
    let week_items = [];

    var toranim_cur_week = this.getToranimCurrentWeek(dates);
    week_items.push(<th key="empty">Toran</th>);
    for (var i = 0; i < dates.length; i++) {
      week_items.push(
        <td key={dates[i]}>
          <Select
            id={dates[i]}
            value={this.state.toran_daily}
            onChange={this.handleChange}
            options={this.addOptions(dates[i])}
            components={animatedComponents}
          ></Select>
        </td>
      );
    }
    week_items.push(
      <td key="Yashvatz">
        <Select
          id="Yashvatz"
          defaultValue="-1"
          onChange={this.handleChange}
          options={this.addOptions(
            "yashvatz-" + dates[0] + "-" + dates[dates.length - 1]
          )}
          components={animatedComponents}
        ></Select>
      </td>
    );

    return week_items;
  };

  addOptions = date => {
    let items = [];

    for (var i = 0; i < toranim.length; i++) {
      items.push({
        label: toranim[i],
        value: date,
        key: i
      });
    }
    return items;
  };

  generateHeader = cur_date => {
    let header_days = [];
    header_days.push(<th key="empty"></th>);
    for (var i = 0; i < cur_date.length + 1; i++) {
      if (i != cur_date.length) {
        header_days.push(
          <th className="toran_days" key={cur_date[i]}>
            {week_days[i] + "\n" + cur_date[i]}
          </th>
        );
      } else {
        header_days.push(
          <th className="toran_days" key="Yashvtaz">
            Weekly Yachvatz
          </th>
        );
      }
    }
    return header_days;
  };

  render() {
    var cur_date = this.props.date;
    return (
      <div>
        <div>
          {console.log("444444")}
          {console.log(this.props.date)}
          <table id="Toranim" className="table table-border cell-border">
            <thead>
              <tr>{this.generateHeader(this.props.date)}</tr>
            </thead>
            <tbody>{this.generateTable()}</tbody>
          </table>
        </div>
      </div>
    );
  }
}

export default Toranim;
