import React, { Component } from "react";
import Button from "@material-ui/core/Button";
import Icon from "@material-ui/core/Icon";
import ArrowBackIosIcon from "@material-ui/icons/ArrowBackIos";
import ArrowForwardIosIcon from "@material-ui/icons/ArrowForwardIos";
import "../CSS/Week.css";

class Week extends Component {
  constructor(props) {
    super(props);
    this.state = {
      date: new Date(),
      week_change: 0
    };
  }
  WeekDates = () => {
    console.log("pass");

    console.log(this.state.date);
    return this.state.date;
  };
  WeekBack = () => {
    var date_change =
      this.state.date.getDate() +
      this.state.week_change * 7 +
      ((7 - this.state.date.getDay()) % 7);

    console.log(date_change);
    this.setState({
      week_change: this.state.week_change--,
      date: this.state.date.setDate(date_change)
    });
    console.log(this.state.week_change);
    console.log(this.state.date);
  };

  render() {
    return (
      <div>
        <h1>{this.WeekDates}</h1>
        <div>
          <Button
            className="lefty"
            variant="contained"
            color="primary"
            startIcon={<ArrowBackIosIcon />}
            onClick={this.WeekBack}
          >
            s
          </Button>
        </div>
        <div>
          <Button
            variant="contained"
            color="primary"
            className="righty"
            endIcon={<ArrowForwardIosIcon />}
            onClick={this.WeekBack}
          >
            s
          </Button>
        </div>
      </div>
    );
  }
}

export default Week;
