import React, { Component } from "react";
import Select, { components } from "react-select";
import makeAnimated from "react-select/animated";

const toranim = ["A", "B", "C", "D", "E"];

const animatedComponents = makeAnimated();

const days = [
  "Sunday",
  "Monday",
  "Tuesday",
  "Wendsday",
  "Thursday",
  "Friday",
  "Saturday",
  "Yachvatz"
];

class Toranim extends Component {
  constructor(props) {
    super(props);
    var date = new Date();
    this.state = { start_date: date, toran_daily: null };
  }
  componentWillMount() {
    this.state.start_date.setDate(
      this.state.start_date.getDate() - (this.state.start_date.getDay() % 7)
    );
  }
  handleChange = target_toran => {
    console.log(target_toran);
    this.setState({ toran_daily: target_toran["label"] });
  };

  getCurrentWeek = () => {
    var currentDate = require(moment);

    var weekStart = currentDate.clone().startOf("week");
    var weekEnd = currentDate.clone().endOf("week");

    var days = [];

    for (var i = 0; i <= 6; i++) {
      days.push(
        moment(weekStart)
          .add(i, "days")
          .format("D.MMM")
      );
    }
    console.log(($scope.weekDays = days));
  };

  generateTable = () => {
    let res = [];
    res.push(<tr key="date_header">{this.setForEachDay()}</tr>);

    return res;
  };

  setForEachDay = () => {
    let week_items = [];
    week_items.push(<th key="empty">Toran</th>);
    for (var i = 0; i < days.length; i++) {
      week_items.push(
        <td key={days[i]}>
          <Select
            id={i}
            defaultValue="-1"
            onChange={this.handleChange}
            onClick={this.handlePrint()}
            options={this.addOptions()}
            components={animatedComponents}
          ></Select>
        </td>
      );
    }

    return week_items;
  };
  handlePrint = () => {};
  addOptions = () => {
    let items = [];

    for (var i = 0; i < toranim.length; i++) {
      items.push({
        label: toranim[i],
        value: i,
        key: i
      });
    }
    return items;
  };

  generateHeader = date => {
    let tmp_date = this.state.start_date;
    let header_days = [];
    header_days.push(<th key="empty"></th>);
    for (var i = 0; i < days.length; i++) {
      if (i + 1 != days.length) {
        header_days.push(
          <th className="toran_days" key={days[i]}>
            {tmp_date.setDate(getDate()) + i}
          </th>
        );
      } else {
        header_days.push(
          <th className="toran_days" key={days[i]}>
            {days[i]}
          </th>
        );
      }
    }
    this.getCurrentWeek();
    return header_days;
  };

  render() {
    return (
      <div>
        <table className="table table-border cell-border">
          <thead>
            <tr>{this.generateHeader(props.date)}</tr>
          </thead>
          <tbody>{this.generateTable()}</tbody>
        </table>
      </div>
    );
  }
}

export default Toranim;
